import './App.css';
import { useState } from 'react';
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';



function App() {

const [nombre,setNombre] = useState("");
const [edad,setEdad] = useState(0);
const [pais,setPais] = useState("");
const [cargo,setCargo] = useState("");
const [anios,setAnios] = useState(0);

const [perfumeList,setPerfumes] = useState([]);

const add = () =>{
  axios.post("http://localhost:3001/create", {
    nombre:nombre,
    edad:edad,
    pais:pais,
    cargo:cargo,
    anios:anios
  }).then(()=>{
    getPerfumes();
    alert("perfume registrado")
  });
}

const getPerfumes = () =>{
  axios.get("http://localhost:3001/perfumes").then((response)=>{
     setPerfumes(response.data);
  });
}


  return (
    <div className="container">

    <div className="card text-center">
        <div className="card-header">
          GESTION DE PERFUMES
        </div>
        <div className="card-body">
            <div className="input-group mb-3">
              <span className="input-group-text" id="basic-addon1">Nombre:</span>
              <input type="text" 
              onChange={(event)=>{
                setNombre(event.target.value);
              }}
              className="form-control" placeholder="" aria-label="Username" aria-describedby="basic-addon1" />
              </div>
            <div className="input-group mb-3">
              <span className="input-group-text" id="basic-addon2">Edad:</span>
              <input type="number" 
              onChange={(event)=>{
                setEdad(event.target.value);
              }}
              className="form-control" placeholder="" aria-label="Username" aria-describedby="basic-addon1" />
            </div>  



              <label>País: <input type="text" onChange={(event)=>{
                setPais(event.target.value);
              }}/></label><br/>
              <label>Cargo: <input type="text" onChange={(event)=>{
                setCargo(event.target.value);
              }}/></label><br/>
              <label>Años: <input type="number" onChange={(event)=>{
                setAnios(event.target.value);
              }}/></label><br/>


        </div>
        <div className="card-footer text-muted">
        <button className='btn btn-primary' onClick={add}>Registrar</button>

        {
         perfumeList.map((val,key)=>{
           return <div className=''>{val.nombre} </div>
          })
         }
        </div>
    </div>
    </div>
  );
}

export default App;
