import './App.css';
import { useState } from 'react';
function App() {

const [nombre,setNombre] = useState("");
const [edad,setEdad] = useState(0);
const [pais,setPais] = useState("");
const [cargo,setCargo] = useState("");
const [anios,setAnios] = useState(0);

const mostrarDatos = () =>{
  alert(nombre);
}
  return (
    <div className="App">
      <div className="datos"> 
        <label>Nombre: <input type="text" onChange={(event)=>{
          setNombre(event.target.value);
        }}/></label><br/>
        <label>Edad: <input type="text" onChange={(event)=>{
          setEdad(event.target.value);
        }}/></label><br/>
        <label>País: <input type="text" onChange={(event)=>{
          setPais(event.target.value);
        }}/></label><br/>
        <label>Cargo: <input type="text" onChange={(event)=>{
          setCargo(event.target.value);
        }}/></label><br/>
        <label>Años: <input type="number" onChange={(event)=>{
          setAnios(event.target.value);
        }}/></label><br/>
        <button onClick={mostrarDatos}>Registrar</button>

      </div> 
    </div>
  );
}

export default App;
