import './App.css';
import { useState } from 'react';
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';



function App() {

const [nombre,setNombre] = useState("");
const [edad,setEdad] = useState(0);
const [pais,setPais] = useState("");
const [cargo,setCargo] = useState("");
const [anios,setAnios] = useState(0);

const [perfumeList,setPerfumes] = useState([]);

const add = () =>{
  axios.post("http://localhost:3001/create", {
    nombre:nombre,
    edad:edad,
    pais:pais,
    cargo:cargo,
    anios:anios
  }).then(()=>{
    alert("perfume registrado")
  });
}

const getPerfumes = () =>{
  axios.get("http://localhost:3001/perfumes").then((response)=>{
     setPerfumes(response.data);
  });
}


  return (
    <div class="container">
    <div className="App">
      <div className="datos"> 
        <label>Nombre: <input type="text" onChange={(event)=>{
          setNombre(event.target.value);
        }}/></label><br/>
        <label>Edad: <input type="text" onChange={(event)=>{
          setEdad(event.target.value);
        }}/></label><br/>
        <label>País: <input type="text" onChange={(event)=>{
          setPais(event.target.value);
        }}/></label><br/>
        <label>Cargo: <input type="text" onChange={(event)=>{
          setCargo(event.target.value);
        }}/></label><br/>
        <label>Años: <input type="number" onChange={(event)=>{
          setAnios(event.target.value);
        }}/></label><br/>
        <button className='btn btn-primary' onClick={add}>Registrar</button>


        <div className='lista'>
           <button className='btn btn-primary' onClick={getPerfumes}>Listar</button>

        {
         perfumeList.map((val,key)=>{
           return <div className=''>{val.nombre} </div>
          })
         }
      </div>
      </div> 
    </div>
    <div class="card text-center">
        <div class="card-header">
          Featured
        </div>
        <div class="card-body">
          <h5 class="card-title">Special title treatment</h5>
          <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
          <a href="#" class="btn btn-primary">Go somewhere</a>
        </div>
        <div class="card-footer text-muted">
          2 days ago
        </div>
</div>
    </div>
  );
}

export default App;
